INSERT INTO `pages` VALUES (2,'组织机构',16,'<p>\r\n	<br />\r\n</p>','2013-09-14 07:34:49','2013-10-12 02:15:10'),(3,'办学理念',17,'<div style=\"background-color:#ffffff;margin:0px;font-family:����;color:#333333;\">\r\n	办学理念:尊重与爱\r\n</div>\r\n<div style=\"background-color:#ffffff;margin:0px;font-family:����;color:#333333;\">\r\n	校训:发现自我追求梦想\r\n</div>\r\n<div style=\"background-color:#ffffff;margin:0px;font-family:����;color:#333333;\">\r\n	校风:平等 合作 进取\r\n</div>\r\n<div style=\"background-color:#ffffff;margin:0px;font-family:����;color:#333333;\">\r\n	教风:真诚 执着 求索\r\n</div>\r\n<div style=\"background-color:#ffffff;margin:0px;font-family:����;color:#333333;\">\r\n	学风:积极 自主 研究\r\n</div>','2013-09-16 10:01:20','2013-10-21 03:00:10'),(4,'甘李学校简介',2,'<p style=\"text-indent:2em;font-family:����;color:#002b5e;font-size:9pt;\">\r\n	布吉甘李学校是一所九年一贯制公办学校，位于深圳市龙岗区布吉街道甘李路，相邻的甘坑社区和上李朗社区有近五万人口。于2012年9月开始，该校招收小学一年级和初中一年级共计8个班级，该校的建成，填补了甘坑片区没有公办学校的空白。\r\n</p>\r\n<div class=\"para\">\r\n	甘李学校建筑面积一万六千多平方米，共建教学办公综合楼一栋、食堂一栋、风雨操场3栋及门卫室等附属用房。\r\n</div>','2013-09-16 10:02:31','2013-10-09 14:21:22'),(5,'校长寄语',3,'<p style=\"text-indent:28px;font-family:&quot;Microsoft YaHei&quot;;font-size:14px;background-color:#FFFFFF;\">\r\n	&nbsp;\r\n</p>','2013-09-16 10:03:53','2013-10-12 02:15:46');
/*!40000 ALTER TABLE `pages` ENABLE KEYS */;
UNLOCK TABLES;

